<?phpif(isset($_POST['submit'])){	$to = "kontakt@klangtypen.de";	$from = $_POST['email'];	$name = $_POST['name'];	$subject = "Contact form submission";	$message = $name . " hat das Folgende geschrieben:" . "\n\n" . $_POST['comment'];		$header = "Nachricht von: " . $from;		mail($to, $subject, $message, $header);		echo "<script type='text/javascript'>	$(document).ready(function(){		$('#emailModal').modal({			show: true,			});		});	</script>";			}?>